<div class="modal fade" id="view-user" tabindex="-1" role="dialog" aria-hidden="true">
    <div class="modal-dialog modal-dialog-centered" role="document">
        <div class="modal-content">
            <div class="modal-header">

                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                    <i class="ti-close"></i>
                </button>
            </div>
            <div class="modal-body">

                <div class="row">
                    <div class="col-md-8 offset-2">
                        <h5 class="modal-title">
                            <figure class="avatar avatar-sm mr-3">
                                    <span class="avatar-title bg-warning text-black-50 rounded-pill">
                                        <i class="ti-user"></i>
                                    </span>
                            </figure>
                            Staff Details</h5>
                        <hr>
                        <p class="margin-5-p">FullName : <span class="modal-user-name text-danger "></span></p>
                        <p class="margin-5-p">Email : <span class="modal-user-email text-danger "></span></p>
                        <p class="margin-5-p">Phone : <span class="modal-user-phone text-danger "></span></p>
                        <p class="margin-5-p">Roles : <span class="modal-user-roles "></span></p>
                        <p class="margin-5-p">Status : <span class="modal-user-status "></span></p>
                    </div>
                </div>

            </div>
        </div>
    </div>
</div>
